import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";


export class News extends Component {
  static defaultProps = {
    country: "in",
    pageSize: 20,
    category: "general",
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  cFL = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };
  // apiK = 'a98db098da0b47fb8cc23ec88e1ac083';
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      apiK: "a98db098da0b47fb8cc23ec88e1ac083",
      totalArticles: 0,
    };
    document.title = `${this.cFL(this.props.category)} - DailyAffairs`;
  }

  async updateNews() {
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.state.apiK}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    if (parsedData.status === "ok") {
      this.setState({
        articles: parsedData.articles,
        totalArticles: parsedData.totalResults,
        loading: false,
      });
    } else {
      this.setState({
        apiK: "765c8344bb974b888d87cb5221185475",
      });
      const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.state.apiK}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
      this.setState({ loading: true });
      let data = await fetch(url);
      let parsedData = await data.json();
      if (parsedData.status === "ok") {
        this.setState({
          articles: parsedData.articles,
          totalArticles: parsedData.totalResults,
          loading: false,
        });
      }
      else{
        this.setState({
          apiK: "63c957333f134574a5808b85400d9ddd",
        });
        const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.state.apiK}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        this.setState({ loading: true });
        let data = await fetch(url);
        let parsedData = await data.json();
        if (parsedData.status === "ok") {
          this.setState({
            articles: parsedData.articles,
            totalArticles: parsedData.totalResults,
            loading: false,
          });
        }
      }
    }
    return parsedData;
  }
  async componentDidMount() {
    // let url =
    //   `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=a98db098da0b47fb8cc23ec88e1ac083`;
    // this.setState({ loading: true });
    // let data = await fetch(url);
    // let parsedData = await data.json();
    // this.setState({
    //   articles: parsedData.articles,
    //   totalArticles: parsedData.totalResults,
    //   loading: false,
    // });
    this.updateNews();
  }

  handleNextClick = async () => {
    // console.log("Next Clicked");
    // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=a98db098da0b47fb8cc23ec88e1ac083&page=${
    //   this.state.page + 1
    // }&pageSize=20`;
    // this.setState({ loading: true });
    // let data = await fetch(url);
    // let parsedData = await data.json();
    // this.setState({
    //   page: this.state.page + 1,
    //   articles: parsedData.articles,
    //   loading: false,
    // });
    this.setState({ page: this.state.page + 1 });
    this.updateNews();
  };
  handlePrevClick = async () => {

    this.setState({ page: this.state.page - 1 });
    this.updateNews();
  };
  fetchMoreData= async()=>{
    this.setState({ page: this.state.page + 1 });
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.state.apiK}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    if (parsedData.status === "ok") {
      this.setState({
        articles: this.state.articles.concat(parsedData.articles),
        totalArticles: parsedData.totalResults,
        loading: false,
      });
    } else {
      this.setState({
        apiK: "765c8344bb974b888d87cb5221185475",
      });
      const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.state.apiK}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
      this.setState({ loading: true });
      let data = await fetch(url);
      let parsedData = await data.json();
      if (parsedData.status === "ok") {
        this.setState({
          articles: this.state.articles.concat(parsedData.articles),
          totalArticles: parsedData.totalResults,
          loading: false,
        });
      }
      else{
        this.setState({
          apiK: "63c957333f134574a5808b85400d9ddd",
        });
        const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.state.apiK}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        this.setState({ loading: true });
        let data = await fetch(url);
        let parsedData = await data.json();
        if (parsedData.status === "ok") {
          this.setState({
            articles: this.state.articles.concat(parsedData.articles),
            totalArticles: parsedData.totalResults,
            loading: false,
          });
        }
      }
    }
  }

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center my-4" style={{ margin: "40px 40px" }}>
          {this.cFL(this.props.category)} Headlines
        </h1>
        {this.state.loading ? <Spinner /> : null}
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length !== this.state.totalArticles}
          loader={<Spinner />}
        >
        <div className="row">
          {this.state.articles.map((article) => {
              return (
                <div className="col-md-6 col-lg-4 col-xl-3" key="{article.url}">
                  <div className="card-group">
                    <NewsItem
                      key={article.url}
                      title={article.title}
                      description={article.description}
                      imageUrl={article.urlToImage}
                      newsUrl={article.url}
                      source={article.source.name}
                      author={article.author}
                      date={article.publishedAt}
                    />
                  </div>
                </div>
              );
            })}
        </div>
        </InfiniteScroll>
        <div className="container d-flex justify-content-between my-3">
          <button
            disabled={this.state.page < 2}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePrevClick}
          >
            {" "}
            &larr; Previous
          </button>
          <button
            disabled={
              this.state.page + 1 > Math.ceil(this.state.totalArticles / 20)
            }
            type="button"
            className="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
