import React, { Component } from "react";

export class NewsItem extends Component {
  render() {
    let { title, description, imageUrl, newsUrl, source, date, author } =
      this.props;
    return (
      <div>
        {/* <div className="card my-2 mx-1">
        <img src={imageUrl?imageUrl:"https://www.reuters.com/resizer/x4mR__Y73SBLtyKl9H7Cy6VldZU=/1200x628/smart/filters:quality(80)/cloudfront-us-east-2.images.arcpublishing.com/reuters/V52IBJMFUFKX5JCW4IDDVBBRUI.jpg"} className="card-img-top" alt="..." />
        <div className="card-body">
          <h5 className="card-title">{title}</h5>
          <p className="card-text">{description}</p>
          <h6 className='text-center'><a href={newsUrl} target="_blank" className="btn btn-primary text-center">Read More</a></h6>
        </div>
      </div> */}
        <div class="card">
          <img
            src={
              imageUrl
                ? imageUrl
                : "https://www.reuters.com/resizer/x4mR__Y73SBLtyKl9H7Cy6VldZU=/1200x628/smart/filters:quality(80)/cloudfront-us-east-2.images.arcpublishing.com/reuters/V52IBJMFUFKX5JCW4IDDVBBRUI.jpg"
            }
            className="card-img-top"
            alt="..."
          />

          <span class="badge bg-info text-dark">{source}</span>
          <div class="card-body">
            <h5 class="card-title">{title}</h5>
            <p class="card-text">{description}</p>
            <p>
              <a
                rel="noreferrer"
                href={newsUrl}
                target="_blank"
                className="btn btn-dark text-center"
              >
                Read More
              </a>
            </p>
          </div>
          <div class="card-footer">
            <small class="text-muted">
              By {author ? author : "unknown"} on {new Date(date).toGMTString()}
            </small>
          </div>
        </div>
      </div>
    );
  }
}

export default NewsItem;
