    };
    document.title= `${this.cFL(this.props.category)} - DailyAffairs`;
  }
async updateNews(){
  const url =
  `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.state.apiK}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
this.setState({ loading: true });
let data = await fetch(url);
let parsedData = await data.json();
if(parsedData.status === "ok"){
  this.setState({
    articles: parsedData.articles,
    totalArticles: parsedData.totalResults,
    loading: false,
  });
  console.log(this.apiK);
